import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DetailView = (id) => {
    const [watches, setWatches] = useState([]);
    useEffect(() => {
        fetchWatches();
    }, []);

    const swal = require('sweetalert2')
    const fetchWatches = async () => {
        try {
            const response = await axios.get('http://127.0.0.1:8000/dashboard/api/watch/'); 
            console.log(response.data)
            setWatches(response.data); 
           
        } catch (error) {
            console.error('Error fetching watches:', error);
        }
    };
    const [watch, setWatch] = useState(null);

  useEffect(() => {
    const id = id.match.watch.id;
    axios.get(`http://127.0.0.1:8000/dashboard/api/watches/${id}/`)
      .then(response => {
        setWatch(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, [id.match.watch.id]);

  if (!watch) {
    return <div>Loading...</div>;
  }
    function handleDelete(id) {
        if (window.confirm('Are you sure you want to delete this watch?')) {
          fetch(`http://127.0.0.1:8000/dashboard/api/watch/${id}/`, {
            method: 'DELETE',
            headers: {
              'Content-Type': 'application/json',
            }
          })
          .then(response => {
            if (response.ok) {
                 
            swal.fire({
                title: "Remove Watch Successfully ",
                icon: "error",
                toast: true,
                timer: 6000,
                position: 'top-right',
                timerProgressBar: true,
                showConfirmButton: false
            })
              setWatches(watches.filter(watch => watch.id !== id));
            } else {
              console.error('Error deleting watch:', response.status);
            }
          })
          .catch(error => {
            console.error('Error deleting watch:', error);
          });
        }
      }
      const [activeTab, setActiveTab] = useState('product-1');

      const handleTabChange = (tabId) => {
          setActiveTab(tabId);
      };

    return (
        <div className="main-content">

        <div className="page-content">
            <div className="container-fluid">

                <div className="row">
                    <div className="col-12">
                        <div className="page-title-box d-sm-flex align-items-center justify-content-between">
                            <h4 className="mb-sm-0 font-size-18">Watch View</h4>
                        </div>
                    </div>
                </div>

                <div className="row">
            <div className="col-lg-12">
                <div className="card">
                    <div className="card-body">
                        <div className="row">
                            {/* Product Images */}
                            <div className="col-xl-6">
                                {/* Product Image Tabs */}
                                <div className="product-detai-imgs">
                                    <div className="row">
                                        {/* Tab Navigation */}
                                            <div className="col-md-2 col-sm-3 col-4">
                                                <div className="nav flex-column nav-pills " id="v-pills-tab" role="tablist" aria-orientation="vertical">
                                                    {/* Tab Link 1 */}
                                                    <a className={`nav-link ${activeTab === 'product-1' ? 'active' : ''}`} onClick={() => handleTabChange('product-1')} role="tab" aria-selected={activeTab === 'product-1'}>
                                                        <img src="assets/images/product/img-7.png" alt="" className="img-fluid mx-auto d-block rounded" />
                                                    </a>
                                                    {/* Tab Link 2 */}
                                                    <a className={`nav-link ${activeTab === 'product-2' ? 'active' : ''}`} onClick={() => handleTabChange('product-2')} role="tab" aria-selected={activeTab === 'product-2'}>
                                                        <img src="assets/images/product/img-8.png" alt="" className="img-fluid mx-auto d-block rounded" />
                                                    </a>
                                                    <a className={`nav-link ${activeTab === 'product-3' ? 'active' : ''}`} onClick={() => handleTabChange('product-2')} role="tab" aria-selected={activeTab === 'product-2'}>
                                                        <img src="assets/images/product/img-8.png" alt="" className="img-fluid mx-auto d-block rounded" />
                                                    </a>
                                                    {/* Tab Link 3 */}
                                                    <a className={`nav-link ${activeTab === 'product-3' ? 'active' : ''}`} onClick={() => handleTabChange('product-3')} role="tab" aria-selected={activeTab === 'product-3'}>
                                                        <img src="assets/images/product/img-9.png" alt="" className="img-fluid mx-auto d-block rounded" />
                                                    </a>
                                                    {/* Tab Link 4 */}
                                                    <a className={`nav-link ${activeTab === 'product-4' ? 'active' : ''}`} onClick={() => handleTabChange('product-4')} role="tab" aria-selected={activeTab === 'product-4'}>
                                                        <img src="assets/images/product/img-10.png" alt="" className="img-fluid mx-auto d-block rounded" />
                                                    </a>
                                                    {/* Add more tab links here */}
                                                </div>
                                            </div>

                                        {/* Tab Content */}
                                        <div className="col-md-7 offset-md-1 col-sm-9 col-8">
                                           {/* Inside the tab-content div */}
                                                <div className="tab-content" id="v-pills-tabContent">
                                                    {/* Tab Pane 1 */}
                                                    <div className={`tab-pane fade ${activeTab === 'product-1' ? 'show active' : ''}`} id="product-1" role="tabpanel" aria-labelledby="product-1-tab">
                                                        <div>
                                                            <img src="assets/images/product/img-7.png" alt="" className="img-fluid mx-auto d-block" />
                                                        </div>
                                                    </div>
                                                    {/* Tab Pane 2 */}
                                                    <div className={`tab-pane fade ${activeTab === 'product-2' ? 'show active' : ''}`} id="product-2" role="tabpanel" aria-labelledby="product-2-tab">
                                                        <div>
                                                            <img src="assets/images/product/img-8.png" alt="" className="img-fluid mx-auto d-block" />
                                                        </div>
                                                    </div>
                                                    {/* Tab Pane 3 */}
                                                    <div className={`tab-pane fade ${activeTab === 'product-3' ? 'show active' : ''}`} id="product-3" role="tabpanel" aria-labelledby="product-3-tab">
                                                        <div>
                                                            <img src="assets/images/product/img-9.png" alt="" className="img-fluid mx-auto d-block" />
                                                        </div>
                                                    </div>
                                                    {/* Tab Pane 4 */}
                                                    <div className={`tab-pane fade ${activeTab === 'product-4' ? 'show active' : ''}`} id="product-4" role="tabpanel" aria-labelledby="product-4-tab">
                                                        <div>
                                                            <img src="assets/images/product/img-10.png" alt="" className="img-fluid mx-auto d-block" />
                                                        </div>
                                                    </div>
                                                    {/* Add more tab panes here */}
                                                </div>

                                            {/* Buttons */}
                                            <div className="text-center">
                                                <button type="button" className="btn btn-primary waves-effect waves-light mt-2 me-1">
                                                    <i className="bx bx-cart me-2"></i> Add to cart
                                                </button>
                                                <button type="button" className="btn btn-success waves-effect  mt-2 waves-light">
                                                    <i className="bx bx-shopping-bag me-2"></i>Buy now
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            {/* Product Details */}
                            
                            <div className="col-xl-6">
                                <div className="mt-4 mt-xl-3">
                                    <a href="javascript: void(0);" className="text-primary">Headphone</a>
                                    <h4 className="mt-1 mb-3">Wireless Headphone (Black)</h4>

                                    <h6 className="text-success text-uppercase">20 % Off</h6>
                                    <h5 className="mb-4">Price : <span className="text-muted me-2"><del>$240 USD</del></span> <b>$225 USD</b></h5>
                                    <p className="text-muted mb-4">To achieve this, it would be necessary to have uniform grammar pronunciation and more common words If several languages coalesce</p>
                                    <div className="row mb-3">
                                        <div className="col-md-6">
                                            <div>
                                                <p className="text-muted"><i className="bx bx-unlink font-size-16 align-middle text-primary me-1"></i> Wireless</p>
                                                <p className="text-muted"><i className="bx bx-shape-triangle font-size-16 align-middle text-primary me-1"></i> Wireless Range : 10m</p>
                                                <p className="text-muted"><i className="bx bx-battery font-size-16 align-middle text-primary me-1"></i> Battery life : 6hrs</p>
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div>
                                                <p className="text-muted"><i className="bx bx-user-voice font-size-16 align-middle text-primary me-1"></i> Bass</p>
                                                <p className="text-muted"><i className="bx bx-cog font-size-16 align-middle text-primary me-1"></i> Warranty : 1 Year</p>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        {/* end row */}
                        
                        <div className="mt-5">
                            <h5 className="mb-3">Specifications :</h5>

                            <div className="table-responsive">
                                <table className="table mb-0 table-bordered">
                                    <tbody>
                                        <tr>
                                            <th scope="row" style={{ width: '400px' }}>Category</th>
                                            <td>Headphone</td>
                                        </tr>
                                        <tr>
                                            <th scope="row">Brand</th>
                                            <td>JBL</td>
                                        </tr>
                                        <tr>
                                            <th scope="row">Color</th>
                                            <td>Black</td>
                                        </tr>
                                        <tr>
                                            <th scope="row">Connectivity</th>
                                            <td>Bluetooth</td>
                                        </tr>
                                        <tr>
                                            <th scope="row">Warranty Summary</th>
                                            <td>1 Year</td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
                {/* end card */}
            </div>
        </div>
    
        </div> 
    </div>

    
    <footer className="footer">
        <div className="container-fluid">
            <div className="row">
                <div className="col-sm-6">
                    <script>document.write(new Date().getFullYear())</script> © React Dashboard.
                </div>
                <div className="col-sm-6">
                    <div className="text-sm-end d-none d-sm-block">
                        Design & Develop by Sarfas
                    </div>
                </div>
            </div>
        </div>
    </footer>
</div>




    );
};

export default DetailView;
