import React, {useState, useEffect} from 'react'
import useAxios from "../utils/useAxios"
import { jwtDecode } from 'jwt-decode'
import AuthContext from '../context/AuthContext'

const ViewDashboard = () => {
    const [users, setUsers] = useState([]);
    const [transactions, setTransaction] = useState([]);
    useEffect(() => {
        fetchusers();
        fetchTransaction();
    }, []);


    const fetchTransaction = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/dashboard/transaction/'); 
            if (!response.ok) {
                throw new Error('Failed to fetch transactions');
            }
            const data = await response.json();
            setTransaction(data);
        } catch (error) {
            console.error('Error fetching transactions:', error);
        }
    };
    const fetchusers = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/dashboard/users/'); 
            if (!response.ok) {
                throw new Error('Failed to fetch users');
            }
            const data = await response.json();
            setUsers(data);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };
    const [response, setResponse] = useState("")
    const api = useAxios();
    const token = localStorage.getItem("authTokens")
  
    
    const decode = jwtDecode(token)
    let user_id = decode.user_id
    let username = decode.username
    let email = decode.email
    let full_name = decode.full_name
    
  
    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await api.get("/test/")
          setResponse(response.data.response)
  
        } catch (error) {
          console.log(error)
          setResponse("Something Went Wrong")
        }
      }
      
      fetchData()
      
    }, [])
  
    console.log('users:', users);
    console.log('transactions:', transactions);
    return (
        <div className="main-content">

        <div className="page-content">
            <div className="container-fluid">

                <div className="row">
                    <div className="col-12">
                        <div className="page-title-box d-sm-flex align-items-center justify-content-between">
                            <h4 className="mb-sm-0 font-size-18">Users</h4>
                        </div>
                    </div>
                </div>
                <div className="row">
                    <div className="col-xl-4">
                        <div className="card">
                            <div className="card-body">
                            <div className="float-end dropdown ms-2">
                                <a className="text-muted" href="#" role="button" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                <i className="mdi mdi-dots-horizontal font-size-18"></i>
                                </a>
                            </div>
                            <div>
                                <div className="mb-4 me-3">
                                <i className="mdi mdi-account-circle text-primary h1"></i>
                                </div>
                                <div>
                                <h5>{username}</h5>
                                <p className="text-muted mb-1">{email}</p>
                                <p className="text-muted mb-0">Id no: #SK0234</p>
                                </div>
                            </div>
                            </div>
                        </div>
                        </div>
                    <div className="col-xl-4">
                        <div className="card">
                            <div className="card-body">
                                <h4 className="card-title mb-4">Transactions</h4>

                                <div className="tab-content mt-4">
                                    <div className="tab-pane active" id="transactions-all-tab" role="tabpanel">
                                    <div className="table-responsive" data-simplebar style={{maxHeight: '330px'}}>
                                        <table className="table align-middle table-nowrap">
                                        <tbody>
                                        {transactions.map(transaction => (
                                        <tr  key={transaction.id}>
                                            <td style={{width: '50px'}}>
                                                <div className="font-size-22 text-success">
                                                <i className="bx bx-down-arrow-circle"></i>
                                                </div>
                                            </td>
                            
                                            <td>
                                                <div className="text-end">
                                                <h5 className="font-size-14 mb-0">{transaction.user.username}</h5>
                                                </div>
                                            </td>
                            
                                            <td>
                                                <div className="text-end">
                                                <h5 className="font-size-14 text-muted mb-0">${transaction.amount}</h5>
                                                </div>
                                            </td>
                                            </tr>
                                             ))}
                                        </tbody>
                                        </table>
                                    </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

        <div className="row">
            <div className="col-12">
                <div className="card">
                    <div className="card-body">

                        {/* Table Section */}
                        <div className="table-responsive">
                            <table className="table align-middle table-nowrap table-check">
                                <thead className="table-light">
                                    <tr>
                                    <th className="align-middle">User ID</th>
                                    <th className="align-middle">Username</th>
                                    <th className="align-middle">Phone</th>
                                    <th className="align-middle">Email</th>
                                    <th className="align-middle">Address</th>

                                    </tr>
                                </thead>
                                <tbody>{users.map(user => (
                                <tr key={user.id}>
                                    
